    content= #{content}
where board_no = #{boardNo}

mapper.select:
select *
from tbl_member
where user_id = #{id}
and   user_pw = #{pw}

select count(1)
from tbl_member
where user_id = #{userId}

mapper.delete:
delete from tbl_member
where user_id = #{userId}
and user_pw = #{userPw}

mapper.update:
update tbl_member
set user_name = #{userName},
	user_pw = #{userPw}
where user_id = #{userId}
